import React from 'react';
import './App.css';
import { forInStatement } from '@babel/types';

class App extends React.Component {
  constructor(){
    super()
    this.state = {
      isLoading: false,
      characterData: {},
      fistCharacter: {},
      secondCharacter: {}
    }
  }

  componentDidMount(){
    this.setState({
      isLoading : true
    })

    var firstCharIndex = Math.random() * 88
    var secondCharIndex = Math.random() * 88

    console.log(firstCharIndex)

    fetch("https://swapi.co/api/people/")
      .then(response => response.json())
      .then(data => {
        this.setState({
          isLoading: false,
          characterData: data,
          fistCharacter: data.results[0],
          secondCharacter: data.results[1]
        })
      })

  }

  render() {
    return (
      <div>
        <div>
        <h1>Title Bout App</h1>
        </div>
        <div>
          {this.state.isLoading ? "Loading" : this.state.fistCharacter.name}
        </div>
        <div>
          {this.state.isLoading ? "Loading" : this.state.secondCharacter.name}
        </div>
      </div>
    )
  }

}

export default App;
