import React from 'react';
import './App.css';
import { forInStatement } from '@babel/types';

class App extends React.Component {
  constructor(){
    super()
    this.state = {
      isLoading: false,
      characterData: {},
      fistCharacter: {},
      secondCharacter: {}
    }
  }

  componentDidMount(){
    this.setState({
      isLoading : true
    })

    fetch("https://swapi.co/api/people/")
      .then(response => response.json())
      .then(data => {
        this.setState({
          isLoading: false,
          characterData: data,
          fistCharacter: data.results[1],
          secondCharacter: data.results[2]
        })
      })

  }

  render() {
    return (
      <div>
        <div>
        <h1>Title Bout App</h1>
        </div>
      </div>

    )
  }

}

export default App;
