import React from 'react';
import arrowLeft from "../Images/arrowLeft.png";
import arrowLeft_Hover from "../Images/arrowLeft_Hover.png";
import arrowLeft_Pressed from "../Images/arrowLeft_Pressed.png";
import arrowRight from "../Images/arrowRight.png";

class CharacterChoice extends React.Component {
    constructor(props){
        super()
        this.state = {
            leftArrowSrc: arrowLeft,
            rightArrowSrc: arrowRight
        }
    }

    render() {

        return (
            <div id="CharacterSelection">
                <img id="left" src={this.state.leftArrowSrc} alt="Left Arrow" onClick={this.props.onClickRight} onMouseOver={this.getHoverImage}/>
                <img src={this.state.rightArrowSrc} alt="Right Arrow" onClick={this.props.onClickLeft}/>
            </div>
        );
    }

    getHoverImage(e){
        console.log(e.currentTarget.id)
    }
}

export default CharacterChoice;
