import React from 'react';
import './App.css';
import Header from './Components/Header';
import Content from './Components/Content';
import CharacterChoice from './Components/CharacterChoice';
import jsonScenarios from "./scenarios.js";

/*
  App feature.
    Ability to seelct which character wins in a certain scenario.
*/

// TODO: Keep track of selected Id's so we don't reroll them.
//        Make sure characters can't match up against themselves
//        Reroll scenario
//        See stats for all scenarios

class App extends React.Component {
  constructor(){
    super()
    this.state = {
      scenario: "",
      leftChar: null,
      rightChar: null,
      selectedIndexes: []
    }

    this.newLeftChar = this.newLeftChar.bind(this)
    this.newRightChar = this.newRightChar.bind(this)
  }

  render() {
    return (
      <div>
        <Header scenario={this.state.scenario}/>
        <Content leftChar={this.state.leftChar} rightChar = {this.state.rightChar}/>
        <CharacterChoice onClickLeft={this.newLeftChar} onClickRight={this.newRightChar}/>
      </div>
    )
  }

  componentDidMount(){

    console.log(this.state.selectedIndexes.length)

    var charIndex1 = Math.ceil(Math.random() * 88)
    var charIndex2 = Math.ceil(Math.random() * 88)

    while (this.validationFailed([charIndex1, charIndex2])){
      if(charIndex1 === 17){
        charIndex1 = Math.ceil(Math.random() * 88)
      }

      if(charIndex2 === 17){
        charIndex2 = Math.ceil(Math.random() * 88)
      }
    }

    var charURL1 = "https://swapi.co/api/people/" + charIndex1
    var charURL2 = "https://swapi.co/api/people/" + charIndex2

    var leftChar = null;
    var rightChar = null;

    Promise.all([
      fetch(charURL1).then(response => response.json()),
      fetch(charURL2).then(response => response.json())
    ]).then(characters => {

      Promise.all([
        fetch(characters[0].species).then(response => response.json()).then(species => {
          leftChar = this.getCharacterObject(characters[0], species, charIndex1)
        }),  
        fetch(characters[1].species).then(response => response.json()).then(species => {
          rightChar = this.getCharacterObject(characters[1], species, charIndex2)
        })
      ]).then(data => {
        this.setState({
          scenario: this.getScenario(),
          leftChar: leftChar,
          rightChar: rightChar
        })
      })
    })
  }

  validationFailed(arr){
      if(arr[0] === 17 || arr[1] === 17){
        console.log("17 selected. Invalid choice. Picking again.")
        return true;
      }

      if(arr[0] === arr[1]){
        console.log("Same characters selected")
        return true;
      }

    return true || false
  }

  getScenario(){
    return jsonScenarios.scenarios[Math.floor(Math.random() * jsonScenarios.scenarios.length)]
  }

  newLeftChar(){
    var charIndex1 = Math.ceil(Math.random() * 88)
    var charURL1 = "https://swapi.co/api/people/" + charIndex1
    var leftChar = null;

    fetch(charURL1).then(response => response.json()).then(character => {
      fetch(character.species).then(response => response.json()).then(species => {
        leftChar = this.getCharacterObject(character, species, charIndex1)
      }).then(data => {
        this.setState({
          leftChar: leftChar
        })
      })
    })
  }

  newRightChar(){
    var charIndex1 = Math.ceil(Math.random() * 88)
    var charURL1 = "https://swapi.co/api/people/" + charIndex1
    var rightChar = null;

    fetch(charURL1).then(response => response.json()).then(character => {
      fetch(character.species).then(response => response.json()).then(species => {
        rightChar = this.getCharacterObject(character, species, charIndex1)
      }).then(data => {
        this.setState({
          rightChar: rightChar
        })
      })
    })
  }

  getCharacter(){
    var charIndex = Math.ceil(Math.random() * 88)

    // Index 17 is not available on swapi.co
    while(charIndex === 17){
      charIndex = Math.ceil(Math.random() * 88)
    }

    var charURL = "https://swapi.co/api/people/" + charIndex

    fetch(charURL).then(response => response.json()).then((foundCharacter) => {
      fetch(foundCharacter.species).then(response => response.json()).then((species) => {

        var character = this.getCharacterObject(foundCharacter, species, charIndex)
        return character
      })
    })
  }

  getCharacterObject(character, species, index){
    return {
        index: index,
        name: character.name,
        species: species.name,
        gender: character.gender,
        birth_year: character.birth_year,
        hair_color: character.hair_color,
        mass: character.mass
    }
  }
}

export default App;
