import React from 'react';
import arrowLeft from "../Images/arrowLeft.png";
import arrowLeft_Hover from "../Images/arrowLeft_Hover.png";
import arrowLeft_Pressed from "../Images/arrowLeft_Pressed.png";
import arrowRight from "../Images/arrowRight.png";

class CharacterChoice extends React.Component {
    constructor(props){
        super()
    }

    render() {

        return (
            <div id="CharacterSelection">
                <img ref="left" src={arrowLeft} alt="Left Arrow" onClick={this.props.onClickRight} onMouseOver={this.getHoverImage("left")}/>
                <img ref="right" src={arrowRight} alt="Right Arrow" onClick={this.props.onClickLeft}/>
            </div>
        );
    }

    getHoverImage(direction){
        if(direction === "left"){
            this.refs.left.src = arrowLeft_Hover;
        }
    }
}

export default CharacterChoice;
