import React from 'react';
import './App.css';
import Header from './Components/Header';
import Content from './Components/Content';
import Reroll from './Components/Reroll';

/*
  App feature.
    Ability to seelct which character wins in a certain scenario.

  Scenarios
    Fight
*/

class App extends React.Component {
  render() {
    return (
      <div>
        <Header scenario={this.getScenario()}/>
        <Content />
        <Reroll event={this.getCharacter}/>
      </div>
    )
  }

  getScenario(){
    fetch("./scenarios.json")
      .then(response => response.json())
      .then(data => {
        var randNum = Math.random() * data.length
        return data[randNum]
      })
  }
}

export default App;
