import React from 'react';

class Character extends React.Component {
    constructor(props){
        super()
        this.state = {
            index: props.index
        }
    }


    render(){
        var imgElement = null
        var imgStyle = null

        if(this.props.index !== -5){
            imgStyle = {
                width: "35%"
            }

            imgElement = React.createElement(
                "img",
                {src: require("../characters/"+ this.props.index + ".jpg"),
                style: imgStyle
                }
            )
        }

        var weightText = ""

        if(this.props.character.mass === "unknown"){
            weightText = "Unknown"
        }else{
            weightText = this.props.character.mass + "kg"
        }

        var ageText = ""

        if(this.props.character.birth_year === "unknown"){
            ageText = "N/A"
        }else{
            ageText = this.props.character.birth_year
        }
        
        return(
            <div className="column">
                <div>
                    <table className="CharacterTable" cellSpacing="10">
                        <tbody>
                            <tr>
                                <td>{imgElement}</td>
                            </tr>
                            <tr>
                                <td>{this.props.character.name}</td>
                            </tr>
                            <tr>
                                <td>{this.props.species}</td>
                            </tr>
                            <tr>
                                <td>{this.props.character.gender}</td>
                            </tr>
                            <tr>
                                <td>{this.props.character.birth_year}</td>
                            </tr>
                            <tr>
                                <td>{this.props.character.hair_color}</td>
                            </tr>
                            <tr>
                                <td>{weightText}</td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}

export default Character
