import './App.css';

class App extends React.Component {
  constructor(){
    super()
    this.state = {
      isLoading: false,
      characterData: {},
      fistCharacter: {},
      secondCharacter: {}
    }
  }

  componentDidMount(){
    this.setState({
      isLoading : true
    })

    fetch("https://swapi.co/api/people/")
      .then(response => response.json())
      .then(data => {
        this.setState({
          isLoading: false,
          characterData: data
        })
      })

    fistCharIndex
  }

  render() {
    text = ""

    if (this.state.isLoading){
      text = "Loading"
    }else{
      text = this.state.characterData[Math.random() * this.state.characterData.length].name
    }

    return (
      <div>
        <div>
        <h1>Title Bout App</h1>
      </div>
      <div>
        {text}        
      </div>
      </div>

    )
  }

}

export default App;
