import React from 'react';
import './App.css';
import Header from './Components/Header';
import Content from './Components/Content';
import CharacterChoice from './Components/CharacterChoice';
import jsonScenarios from "./scenarios.js";

/*
  App feature.
    Ability to seelct which character wins in a certain scenario.
*/

class App extends React.Component {
  constructor(){
    super()
    this.state = {
      scenario: ""
    }
  }

  render() {
    return (
      <div>
        <Header scenario={this.state.scenario}/>
        <Content />
        <CharacterChoice />
      </div>
    )
  }

  componentDidMount(){
    this.getScenario()
  }

  getScenario(){
    this.setState({
      scenario: jsonScenarios.scenarios[Math.floor(Math.random() * jsonScenarios.scenarios.length)]
    })
  }

  getCharacter(){
    this.setState({
        isLoading: true
    })

    var charIndex = Math.ceil(Math.random() * 88)

    // Index 17 is not available on swapi.co
    while(charIndex === 17){
      charIndex = Math.ceil(Math.random() * 88)
    }

    var charURL = "https://swapi.co/api/people/" + charIndex

    fetch(charURL).then(response => response.json()).then((foundCharacter) => {
      fetch(foundCharacter.species).then(response => response.json()).then((species) => {

        var character = this.getCharacterObject(foundCharacter, species, charIndex)

        //console.log(character)

        this.setState({
            isLoading: false,
            character: character
        })
      })
    })
  }

  getCharacterObject(character, species, index){
    return {
        index: index,
        name: character.name,
        species: species.name,
        gender: character.gender,
        birth_year: character.birth_year,
        hair_color: character.hair_color,
        mass: character.mass
    }
  }
}

export default App;
