import React from 'react';
import './App.css';
import Header from './Components/Header';
import Content from './Components/Content';
import Reroll from './Components/Reroll';

class App extends React.Component {
  constructor(){
    super()
    this.state = {
      isLoading: false,
      firstCharacter: {},
      secondCharacter: {},
      firstCharIndex: -5,
      secondCharIndex: -5
    }

    this.getCharacters = this.getCharacters.bind(this)
  }

  componentDidMount(){
    this.getCharacters()
  }

  getCharacters(){
    this.setState({
      isLoading : true
    })

    var firstCharIndex = Math.ceil(Math.random() * 88)
    var secondCharIndex = Math.ceil(Math.random() * 88)

    while(firstCharIndex === 17){
      firstCharIndex = Math.ceil(Math.random() * 88)
    }

    while(secondCharIndex === 17){
      secondCharIndex = Math.ceil(Math.random() * 88)
    }

    while(firstCharIndex === secondCharIndex){
      secondCharIndex = Math.ceil(Math.random() * 88)
    }

    var firstCharURL = "https://swapi.co/api/people/" + firstCharIndex
    var secondCharURL = "https://swapi.co/api/people/" + secondCharIndex

    Promise.all([
      fetch(firstCharURL).then(response => response.json()),
      fetch(secondCharURL).then(response => response.json())
    ]).then((characters) => {
      Promise.all([
        fetch(characters[0].species).then(response => response.json()),
        fetch(characters[1].species).then(response => response.json())
      ]).then((species) => {

        var firstCharacter = {
          name: characters[0].name,
          species: species[0].name,
          gender: characters[0].gender,
          birth_year: characters[0].birth_year,
          hair_color: characters[0].hair_color,
          mass: characters[0].mass
        }

        var secondCharacter = {
          name: characters[1].name,
          species: species[1].name,
          gender: characters[1].gender,
          birth_year: characters[1].birth_year,
          hair_color: characters[1].hair_color,
          mass: characters[1].mass
        }

        this.setState({
          isLoading : false,
          firstCharacter : firstCharacter,
          secondCharacter : secondCharacter,
          firstCharIndex: firstCharIndex,
          secondCharIndex: secondCharIndex
        })
      })
    })    
  }

  render() {
    return (
      <div>
        <Header />
        <Content isLoading={this.state.isLoading}
          firstChar={this.state.firstCharacter}
          secondChar={this.state.secondCharacter}
          firstCharIndex={this.state.firstCharIndex}
          secondCharIndex={this.state.secondCharIndex} />
        <Reroll event={this.getCharacters}/>
      </div>
    )
  }

}

export default App;
