import React from 'react';
import './App.css';
import Header from './Components/Header';
import Content from './Components/Content';
import Reroll from './Components/Reroll';

/*
  App feature.
    Ability to seelct which character wins in a certain scenario.

  Scenarios
    Fight
*/

class App extends React.Component {
  constructor(){
    super()

    this.getCharacters = this.getCharacters.bind(this)
  }



  getCharacter(){
    this.setState({
      isLoading : true
    })

    var firstCharIndex = Math.ceil(Math.random() * 88)

    // Index 17 is not available on swapi.co
    while(firstCharIndex === 17){
      firstCharIndex = Math.ceil(Math.random() * 88)
    }

    var firstCharURL = "https://swapi.co/api/people/" + firstCharIndex

      fetch(firstCharURL).then(response => response.json()).then((characters) => {
        fetch(characters[0].species).then(response => response.json()).then((species) => {

        var firstCharacter = this.getCharacterObject(characters[0], species[0], firstCharIndex)

        this.setState({
          isLoading : false,
          firstCharacter : firstCharacter,
        })
      })
    })    
  }

  render() {
    return (
      <div>
        <Header />
        <Content getChar={this.getCharacters}
        />
        <Reroll event={this.getCharacters}/>
      </div>
    )
  }

  getCharacterObject(character, species, index){
    return {
      index: index,
      name: character.name,
      species: species.name,
      gender: character.gender,
      birth_year: character.birth_year,
      hair_color: character.hair_color,
      mass: character.mass
    }
  }

}

export default App;
