import React from 'react';
import './App.css';
import Header from './Components/Header';
import Content from './Components/Content';
import Reroll from './Components/Reroll';

/*
  App feature.
    Ability to seelct which character wins in a certain scenario.

  Scenarios
    Fight
*/

class App extends React.Component {
  render() {
    return (
      <div>
        <Header />
        <Content scenario={this.getScenario()}/>
        <Reroll event={this.getCharacter}/>
      </div>
    )
  }

  getScenario(){
  return "win in a fight to the death?"
  }
}

export default App;
