import React from 'react';
import "../App.css";

class Canvas extends React.Component {
    
    render(){
        return(
            <div>
                <canvas ref="canvas" id="canvas" width={document.width}></canvas>
            </div>
        )
    }

    componentDidMount(){
        this.updateCanvas()
    }

    updateCanvas(){
        const ctx = this.refs.canvas.getContext("2d");

        ctx.fillStyle = "#FF5555";
        ctx.fillRect(0, 0, window.width, 100);
    }
}

export default Canvas
