import React from 'react';

class Character extends React.Component {
    constructor(props){
        super()
        this.state = {
            index: props.index
        }
    }


    render(){
        var imgElement = null

        if(this.props.index != -5){
            imgElement = React.createElement(
                "img",
                {src: require("../characters/"+ this.props.index + ".jpg"),
                width: "50%"}
            )
        }

        var newDiv = null
        var divStyle = null

        if(this.props.index != -5){
            console.log("load image?")
            divStyle = {
                backgroundImage: require("../characters/"+ this.props.index + ".jpg")
            }

            newDiv = React.createElement(
                "div",
                {style: divStyle}
            )
        }
        
        return(
            <div className="column">
                <div>
                    {newDiv}
                    <table className="CharacterTable" cellSpacing="10">
                        <tbody>
                            <tr>
                                <td>{this.props.character.name}</td>
                            </tr>
                            <tr>
                                <td>{this.props.character.gender}</td>
                            </tr>
                            <tr>
                                <td>{this.props.character.birth_year === "unknown" ? "\u00a0" : this.props.character.birth_year}</td>
                            </tr>
                            <tr>
                                <td>{this.props.character.hair_color}</td>
                            </tr>
                            <tr>
                                <td>{this.props.character.mass === "unknown" ? "\u00a0" : this.props.character.mass}</td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}

export default Character
