import React from 'react';
import arrowLeft from "../Images/arrowLeft.png";
import arrowLeft_Hover from "../Images/arrowLeft_Hover.png";
import arrowLeft_Pressed from "../Images/arrowLeft_Pressed.png";
import arrowRight from "../Images/arrowRight.png";


class CharacterChoice extends React.Component {
    constructor(props){
        super()
        this.state = {
            leftArrowSrc: arrowLeft,
            rightArrowSrc: arrowRight
        }

        this.handleOver = this.handleOver.bind(this)
    }

    render() {

        return (
            <div id="CharacterSelection">
                <img id="left" src={this.state.leftArrowSrc} alt="Left Arrow" onClick={this.props.onClickRight} onMouseOver={this.handleOver} onMouseOut={this.handleOut}/>
                <img id="right" src={this.state.rightArrowSrc} alt="Right Arrow" onClick={this.props.onClickLeft} onMouseOver={this.handleOver}/>
            </div>
        );
    }

    handleOver(e){
        if(e.currentTarget.id === "left"){
            this.setState({
                leftArrowSrc: arrowLeft_Hover
            })
