        }
    }

    handleOut(e){
        if(e.currentTarget.id === "left"){
            this.setState({
                leftArrowSrc: arrowLeft
            })
        }else if(e.currentTarget.id === "right"){
            
        }
    }
}

export default CharacterChoice;
