import React from 'react';
import './App.css';
import Header from './Components/Header';
import Content from './Components/Content';
import CharacterChoice from './Components/CharacterChoice';
import jsonScenarios from "./scenarios.js";

/*
  App feature.
    Ability to seelct which character wins in a certain scenario.
*/

// TODO: Keep track of selected Id's so we don't reroll them.
//        Make sure characters can't match up against themselves
//        Reroll scenario
//        See stats for all scenarios

class App extends React.Component {
  constructor(){
    super()

    var arr = []

    for(var i = 1; i <= 88; i++){
      arr.push(i)
    }

    this.state = {
      scenario: "",
      leftChar: null,
      rightChar: null,
      selectedIndexes: arr
    }

    this.newLeftChar()
    this.newRightChar()

    this.newLeftChar = this.newLeftChar.bind(this)
    this.newRightChar = this.newRightChar.bind(this)
  }

  render() {
    return (
      <div>
        <Header scenario={this.state.scenario}/>
        <Content leftChar={this.state.leftChar} rightChar = {this.state.rightChar}/>
        <CharacterChoice onClickLeft={this.newLeftChar} onClickRight={this.newRightChar}/>
      </div>
    )
  }

  componentDidMount(){
    this.setState({
      scenario: this.getScenario()
    })
  }

  validationFailed(index){
      if(index === 17){
        console.log("17 selected. Invalid choice. Picking again.")
        return true;
      }

      if(!(index in this.state.selectedIndexes)){
        console.log("Index already picked")
        return true
      }

      if(this.state.leftChar === null || this.state.rightChar === null){
        return false
      }

      if(this.state.leftChar.index === this.state.rightChar.index){
        console.log("Same characters selected")
        return true;
      }

    return false
  }

  getScenario(){
    return jsonScenarios.scenarios[Math.floor(Math.random() * jsonScenarios.scenarios.length)]
  }

  newLeftChar(){

    var charIndex1 = this.getNewCharacterIndex()

    while (this.validationFailed(charIndex1)){
      charIndex1 = this.getNewCharacterIndex()
    }

    var arr = this.state.selectedIndexes.filter(function(item) {
      console.log("Removing: " + charIndex1)
      return item !== charIndex1
    })

    var charURL1 = "https://swapi.co/api/people/" + charIndex1
    var leftChar = null;

    fetch(charURL1).then(response => response.json()).then(character => {
      if(character.species.length === 0){
        // Don't try to fetch species
        leftChar = this.getCharacterObject(character, {name: "Unknown"}, charIndex1)
        this.setState({
          leftChar: leftChar
        })
      }else{
        fetch(character.species).then(response => response.json()).then(species => {
          leftChar = this.getCharacterObject(character, species, charIndex1)
        }).then(data => {
          this.setState({
            leftChar: leftChar,
            selectedIndexes: arr
          })
        })
      }
    })

    console.log(this.state.selectedIndexes)
  }

  newRightChar(){
    var charIndex1 = this.getNewCharacterIndex()    

    while (this.validationFailed(charIndex1)){
      charIndex1 = this.getNewCharacterIndex()
    }

    var arr = this.state.selectedIndexes.filter(function(item) {
      console.log("Removing: " + charIndex1)
      return item !== charIndex1
    })

    var charURL1 = "https://swapi.co/api/people/" + charIndex1
    var rightChar = null;

    fetch(charURL1).then(response => response.json()).then(character => {
      if(character.species.length === 0){
        // Don't try to fetch species
        rightChar = this.getCharacterObject(character, {name: "Unknown"}, charIndex1)
        this.setState({
          leftChar: rightChar,
          selectedIndexes: arr
        })
      }else{
        fetch(character.species).then(response => response.json()).then(species => {
          rightChar = this.getCharacterObject(character, species, charIndex1)
          }).then(data => {
            this.setState({
              rightChar: rightChar
            })
          })
      }
    })

    console.log(this.state.selectedIndexes)
  }

  getCharacterObject(character, species, index){
    return {
        index: index,
        name: character.name,
        species: species.name,
        gender: character.gender,
        birth_year: character.birth_year,
        hair_color: character.hair_color,
        mass: character.mass
    }
  }

  getNewCharacterIndex(){
    var rand = Math.ceil(Math.random() * 88)

    console.log("index: " + rand)

    return rand
  }
}

export default App;
