import React from 'react';
import "../App.css";

class Canvas extends React.Component {
    constructor(){
        super()

        var arr = []

        for(var i = 0; i < 1000; i++){
            arr.push({
                x: Math.random() * window.innerWidth,
                y: Math.random() * window.innerHeight
            });
        }

        this.state = {
            mouseIsDown: false,
            speed: 1,
            stars: arr
        }

        this.mouseUpDown = this.mouseUpDown.bind(this)
        this.doMouseDown = this.doMouseDown.bind(this)
    }
    
    render(){
        return(
            <div>
                <canvas ref="canvas" id="canvas" onMouseDown={this.mouseUpDown} onMouseUp={this.doMouseDown} width={window.innerWidth} height={window.innerHeight}></canvas>
            </div>
        )
    }

    componentDidMount(){
        const canvas = this.refs.canvas;

        canvas.addEventListener("mouseup", this.doMouseDown, false);

        this.updateCanvas()
    }

    updateCanvas(){
        const ctx = this.refs.canvas.getContext("2d");

        //const centreX = this.refs.canvas.width/2;
        //const centreY = this.refs.canvas.height/2;

    
        ctx.fillStyle = "#000";
        ctx.fillRect(0, 0, window.innerWidth, window.innerHeight);

        ctx.fillStyle = "#FFF";
        for(var i = 0; i < this.state.stars.length; i++){
            ctx.beginPath();
            ctx.arc(this.state.stars[i].x, this.state.stars[i].y, Math.floor(Math.random() * 3), 0, Math.PI * 2, false);
            ctx.fill();
        }

        //ctx.translate(centreX, centreY);
    }

    remap(value, low1, high1, low2, high2){
        return low2 + (value - low1) * (high2 - low2) / (high1 - low1);
    }

    mouseUpDown(){
        console.log("mouse working")
        this.setState((prevState) => {
            return {
                mouseIsDown: !prevState.mouseIsDown,
                speed: 50
            }
        })
    }

    doMouseDown(){
        this.setState((prevState) => {
            return {
                mouseIsDown: !prevState.mouseIsDown,
                speed: 1
            }
        })
    }
}

export default Canvas
