import React from 'react';

class CharacterChoice extends React.Component {
    constructor(props){
        super()
    }

    render() {
        return (
            <div id="CharacterSelection">
                <input type="button" value="<" onClick={this.leftChar} direction="left"/>
                <input type="button" value=">" onClick={this.rightChar} direction="right"/>
            </div>
        );
    }

    leftChar = () => {
        this.props.onClick("left")
    }

    rightChar = () => {
        this.props.onClick("right")
    }
}

export default CharacterChoice;
