import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(){
    super()
    this.state = {
      isLoading: false,
      characterData: {}
    }
  }

  componentDidMount(){
    this.setState({
      isLoading : true
    })

    fetch("https://swapi.co/api/people/")
      .then(response => response.json())
      .then(data => {
        this.setState({
          isLoading: false,
          characterData: data
        })
      })
  }

  render() {
    return (
      <div>
        <div>
        <h1>Title Bout App</h1>
      </div>
      <div>
        {this.state.isLoading ? "Loading" : this.state.character.name}
      </div>
      </div>

    )
  }

}

export default App;
