import React from 'react';

class Character extends React.Component {
    constructor(props){
        super()
        this.state = {
            isLoading: true,
            character: {}
        }
    }

    render(){

        if(this.state.isLoading) return(
            <div className="column">
                <h1>Loading...</h1>
            </div>
        )

        var imgStyle = {
            width: "35%"
        }

        if(this.state.character.index){
            var imgElement = React.createElement(
                "img",
                {   // Styles
                    src: require("../characters/"+ this.state.character.index + ".jpg"),
                    style: imgStyle
                }
            )
        }        

        var weightText = ""

        if(this.state.character.mass === "unknown"){
            weightText = "Unknown"
        }else{
            weightText = this.state.character.mass + "kg"
        }

        var ageText = ""

        if(this.state.character.birth_year === "unknown"){
            ageText = "N/A"
        }else{
            ageText = this.state.character.birth_year
        }
        
        return(
            <div className="column">
                <div>
                    <table className="CharacterTable" cellSpacing="10">
                        <tbody>
                            <tr>
                                <td>{imgElement}</td>
                            </tr>
                            <tr>
                                <td>{this.state.character.name}</td>
                            </tr>
                            <tr>
                                <td>{this.state.character.species}</td>
                            </tr>
                            <tr>
                                <td>{this.state.character.gender}</td>
                            </tr>
                            <tr>
                                <td>{ageText}</td>
                            </tr>
                            <tr>
                                <td>{this.state.character.hair_color}</td>
                            </tr>
                            <tr>
                                <td>{weightText}</td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }

    componentDidMount(){
        this.getCharacter()
    }

    getCharacter(){
        this.setState({
            isLoading: true
        })

        var charIndex = Math.ceil(Math.random() * 88)
    
        // Index 17 is not available on swapi.co
        while(charIndex === 17){
          charIndex = Math.ceil(Math.random() * 88)
        }
    
        var charURL = "https://swapi.co/api/people/" + charIndex
    
        fetch(charURL).then(response => response.json()).then((foundCharacter) => {
          fetch(foundCharacter.species).then(response => response.json()).then((species) => {
    
            var character = this.getCharacterObject(foundCharacter, species, charIndex)

            //console.log(character)

            this.setState({
                isLoading: false,
                character: character
            })
          })
        })
    }
    
    getCharacterObject(character, species, index){
    return {
        index: index,
        name: character.name,
        species: species.name,
        gender: character.gender,
        birth_year: character.birth_year,
        hair_color: character.hair_color,
        mass: character.mass
    }
    }
}

export default Character
