import React from 'react';
import './App.css';
import Header from './Components/Header';
import Content from './Components/Content';

class App extends React.Component {
  constructor(){
    super()
    this.state = {
      isLoading: false,
      firstCharacter: {},
      secondCharacter: {},
      firstCharImage: "",
      secondCharImage: ""
    }
  }

  componentDidMount(){
    this.setState({
      isLoading : true
    })

    var firstCharIndex = Math.floor(Math.random() * 88)
    var secondCharIndex = Math.floor(Math.random() * 88)

    var firstCharURL = "https://swapi.co/api/people/" + firstCharIndex
    var secondCharURL = "https://swapi.co/api/people/" + secondCharIndex

    var firstCharImage = "./../characters/" + firstCharIndex + ".jpg"
    var secondCharImage = "../characters/" + secondCharIndex + ".jpg"

    Promise.all([
      fetch(firstCharURL).then(response => response.json()),
      fetch(secondCharURL).then(response => response.json())
    ]).then((value) =>{
      this.setState({
        isLoading : false,
        firstCharacter : value[0],
        secondCharacter : value[1],
        firstCharImage: firstCharImage,
        secondCharImage: secondCharImage
      })
    })    
  }

  render() {
    return (
      <div>
        <Header />
        <Content firstChar={this.state.firstCharacter} secondChar={this.state.secondCharacter} firstCharImage={this.state.firstCharImage} secondCharImage={this.state.secondCharImage} />
      </div>
    )
  }

}

export default App;
