import React from 'react';
import './App.css';
import { forInStatement } from '@babel/types';

class App extends React.Component {
  constructor(){
    super()
    this.state = {
      isLoading: false,
      fistCharacter: {},
      secondCharacter: {}
    }
  }

  componentDidMount(){
    this.setState({
      isLoading : true
    })

    var firstCharIndex = Math.floor(Math.random() * 88)
    var secondCharIndex = Math.floor(Math.random() * 88)

    var firstCharURL = "https://swapi.co/api/people/" + firstCharIndex
    var secondCharURL = "https://swapi.co/api/people/" + secondCharIndex

    var firstChar = fetch(firstCharURL).then(response => response.json())

    this.setState(prevState => {
      var newState = {
        isLoading : !prevState.isLoading,
        fistCharacter : firstChar,
        secondCharacter : fetch(secondCharURL).then(response => response.json())
      }
      return newState
    })

    /*
    fetch("https://swapi.co/api/people/")
      .then(response => response.json())
      .then(data => {
        this.setState({
          isLoading: false,
          characterData: data,
          fistCharacter: data.results[0],
          secondCharacter: data.results[1]
        })
      })
    */

  }

  render() {
    return (
      <div>
        <div>
        <h1>Title Bout App</h1>
        </div>
        <div>
          {this.state.isLoading ? "Loading" : this.state.fistCharacter.name}
        </div>
        <div>
          {this.state.isLoading ? "Loading" : this.state.secondCharacter.name}
        </div>
      </div>
    )
  }

}

export default App;
