import React from 'react';
import './App.css';
import Header from './Components/Header';
import Content from './Components/Content';
import Reroll from './Components/Reroll';
import jsonScenarios from "./scenarios.js";

/*
  App feature.
    Ability to seelct which character wins in a certain scenario.

  Scenarios
    Fight
*/

class App extends React.Component {
  constructor(){
    super()
    this.state = {
      scenario: ""
    }
  }

  render() {
    return (
      <div>
        <Header scenario={this.state.scenario}/>
        <Content />
        <Reroll event={this.getCharacter}/>
      </div>
    )
  }

  componentDidMount(){
    this.getScenario()
  }

  getScenario(){
    this.setState({
      scenario: jsonScenarios.scenarios[Math.floor(Math.random() * jsonScenarios.scenarios.length)]
    })
  }
}

export default App;
