import React from 'react';
import './App.css';
import { forInStatement } from '@babel/types';

class App extends React.Component {
  constructor(){
    super()
    this.state = {
      isLoading: false,
      characterData: {},
      fistCharacter: {},
      secondCharacter: {}
    }
  }

  componentDidMount(){
    this.setState({
      isLoading : true
    })

    fetch("https://swapi.co/api/people/")
      .then(response => response.json())
      .then(data => {
        this.setState({
          isLoading: false,
          characterData: data
        })
      })
      .then(() => {
        this.setState((prevState) => {
          var newState = {
            fistCharacter : prevState.characterData[Math.random() * 87],
            secondCharacter : prevState.characterData[Math.random() * 87]
          }
            return newState
        })

      })

  }

  render() {
    return (
      <div>
        <div>
        <h1>Title Bout App</h1>
        </div>
        <div>
          {this.state.fistCharacter.name}        
        </div>
        <div>
        {this.state.secondCharacter.name}
        </div>
      </div>

    )
  }

}

export default App;
